package Sirassignment;

public class ReverseTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	for(int i=1;i<=5;i++)//1
		{
			for (int j=1;j<=i;j++)
				System.out.print("*");
			System.out.println();
		}
	for (int i=1;i<=5;i++) //2
		   {
			   for(int j=i;j<=5;j++)
				   System.out.print("*");
			   System.out.println();
		   }
			

	}

}